import android.app.AlarmManager
import android.content.Context
import android.content.Intent
import android.os.Build
import android.os.Bundle
import android.provider.Settings
import android.view.MenuItem
import android.widget.Toast
import androidx.appcompat.app.AppCompatActivity
import androidx.core.net.toUri
import androidx.preference.Preference
import androidx.preference.PreferenceFragmentCompat
import androidx.preference.SwitchPreferenceCompat
import com.example.teacherscheduler.R
import com.example.teacherscheduler.data.Repository
import com.example.teacherscheduler.data.SettingsManager
import com.example.teacherscheduler.model.Semester

/**
 * Settings activity that uses the PreferenceFragmentCompat for displaying settings
 */
class SettingsActivity : AppCompatActivity() {

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_settings)

        // Set up the toolbar
        val toolbar = findViewById<androidx.appcompat.widget.Toolbar>(R.id.toolbar)
        setSupportActionBar(toolbar)
        supportActionBar?.setDisplayHomeAsUpEnabled(true)
        supportActionBar?.title = getString(R.string.menu_settings)

        if (savedInstanceState == null) {
            supportFragmentManager
                .beginTransaction()
                .replace(R.id.settings_container, SettingsFragment())
                .commit()
        }
    }

    override fun onOptionsItemSelected(item: MenuItem): Boolean {
        if (item.itemId == android.R.id.home) {
            finish()
            return true
        }
        return super.onOptionsItemSelected(item)
    }

    /**
     * Fragment for displaying preferences
     */
    class SettingsFragment : PreferenceFragmentCompat() {
        private lateinit var repository: Repository
        private lateinit var settingsManager: SettingsManager

        override fun onCreatePreferences(savedInstanceState: Bundle?, rootKey: String?) {
            setPreferencesFromResource(R.xml.preferences, rootKey)

            repository = Repository(requireActivity().application)
            settingsManager = SettingsManager(requireContext())

            setupNotificationPreferences()
            setupAlarmPreferences()
            setupSoundPreferences()
            setupVibrationPreferences()
            setupAdvanceNotificationPreferences()
            setupSemesterPreferences()
            setupAutoSyncPreferences()
            setupAppVersionPreference()
        }

        private fun setupNotificationPreferences() {
            val notificationPref = findPreference<Preference>("notification_permission")
            notificationPref?.setOnPreferenceClickListener {
                if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {
                    val intent = Intent(Settings.ACTION_APP_NOTIFICATION_SETTINGS).apply {
                        putExtra(Settings.EXTRA_APP_PACKAGE, requireActivity().packageName)
                    }
                    startActivity(intent)
                } else {
                    // For older versions, open app details settings
                    val intent = Intent(Settings.ACTION_APPLICATION_DETAILS_SETTINGS).apply {
                        data = "package:${requireActivity().packageName}".toUri()
                    }
                    startActivity(intent)
                }
                true
            }
        }

        private fun setupAlarmPreferences() {
            val alarmPref = findPreference<Preference>("alarm_permission")
            if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.S) {
                alarmPref?.isVisible = true
                alarmPref?.setOnPreferenceClickListener {
                    val alarmManager = requireContext().getSystemService(Context.ALARM_SERVICE) as? AlarmManager
                    if (alarmManager != null && !alarmManager.canScheduleExactAlarms()) {
                        val intent = Intent(Settings.ACTION_REQUEST_SCHEDULE_EXACT_ALARM)
                        startActivity(intent)
                    } else {
                        Toast.makeText(requireContext(), getString(R.string.permission_already_granted), Toast.LENGTH_SHORT).show()
                    }
                    true
                }
            } else {
                alarmPref?.isVisible = false
            }
        }

        private fun setupSoundPreferences() {
            val soundPref = findPreference<SwitchPreferenceCompat>("notification_sound")
            soundPref?.setOnPreferenceChangeListener { _, newValue ->
                val enabled = newValue as Boolean
                val sharedPrefs = requireActivity().getSharedPreferences(
                    "app_settings", 
                    Context.MODE_PRIVATE
                )
                sharedPrefs.edit().putBoolean("notification_sound_enabled", enabled).apply()
                true
            }
        }

        private fun setupVibrationPreferences() {
            val vibrationPref = findPreference<SwitchPreferenceCompat>("notification_vibration")
            vibrationPref?.setOnPreferenceChangeListener { _, newValue ->
                val enabled = newValue as Boolean
                val sharedPrefs = requireActivity().getSharedPreferences(
                    "app_settings", 
                    Context.MODE_PRIVATE
                )
                sharedPrefs.edit().putBoolean("notification_vibration_enabled", enabled).apply()
                true
            }
        }

        private fun setupAdvanceNotificationPreferences() {
            val advancePref = findPreference<SwitchPreferenceCompat>("advance_notification")
            advancePref?.setOnPreferenceChangeListener { _, newValue ->
                val enabled = newValue as Boolean
                val sharedPrefs = requireActivity().getSharedPreferences(
                    "app_settings", 
                    Context.MODE_PRIVATE
                )
                sharedPrefs.edit().putBoolean("advance_notification_enabled", enabled).apply()
                true
            }
        }

        private fun setupSemesterPreferences() {
            // Handle current semester settings
            val currentSemesterPref = findPreference<Preference>("current_semester")
            currentSemesterPref?.setOnPreferenceClickListener {
                val intent = Intent(requireContext(), SemesterSelectorActivity::class.java)
                startActivity(intent)
                true
            }

            // Update current semester summary
            repository.getCurrentSemester().observe(viewLifecycleOwner) { semester: Semester? ->
                currentSemesterPref?.summary = semester?.name ?: getString(R.string.no_semester_selected)
            }

            // Handle manage semesters settings
            val manageSemestersPref = findPreference<Preference>("manage_semesters")
            manageSemestersPref?.setOnPreferenceClickListener {
                val intent = Intent(requireContext(), SemesterManagementActivity::class.java)
                startActivity(intent)
                true
            }
        }

        private fun setupAutoSyncPreferences() {
            val autoSyncPref = findPreference<SwitchPreferenceCompat>("auto_sync")
            autoSyncPref?.setOnPreferenceChangeListener { _, newValue ->
                val enabled = newValue as Boolean
                val settings = settingsManager.getSettings()
                val updatedSettings = settings.copy(autoSyncEnabled = enabled)
                settingsManager.saveSettings(updatedSettings)
                true
            }
        }

        private fun setupAppVersionPreference() {
            val appVersionPref = findPreference<Preference>("app_version")
            appVersionPref?.summary = requireContext().packageManager.getPackageInfo(
                requireContext().packageName, 0
            ).versionName
        }
    }
}

