fun updateSemester(semester: Semester) {
    val index = semesters.indexOfFirst { it.id == semester.id }
    if (index != -1) {
        semesters[index] = semester
        if (semester.isCurrentSemester) {
            setCurrentSemester(semester.id)
        }
    }
}

fun getAllSemesters(): List<Semester> = semesters.toList()

fun getCurrentSemester(): Semester? {
    return semesters.find { it.isCurrentSemester } ?: semesters.firstOrNull()
}

fun getSemesterById(id: Long): Semester? = semesters.find { it.id == id }

