    private lateinit var themeManager: ThemeManager

    override fun onCreateView(inflater: LayoutInflater, container: ViewGroup?, savedInstanceState: Bundle?): View {
        themeManager = ThemeManager(requireContext())

        // Create theme selection UI
        val themeOptions = arrayOf("Light", "Dark", "System Default")
        val builder = AlertDialog.Builder(requireContext())
        builder.setTitle("Choose Theme")
        builder.setSingleChoiceItems(themeOptions, getCurrentThemeIndex()) { dialog, which ->
            when (which) {
                0 -> themeManager.setTheme(ThemeManager.THEME_LIGHT)
                1 -> themeManager.setTheme(ThemeManager.THEME_DARK)
                2 -> themeManager.setTheme(ThemeManager.THEME_SYSTEM)
            }
            dialog.dismiss()
            // Recreate activity to apply theme
            requireActivity().recreate()
        }

        return view
    }
}

