class ProfileActivity : AppCompatActivity() {
    private lateinit var themeManager: ThemeManager
    private lateinit var lightThemeRadio: RadioButton
    private lateinit var darkThemeRadio: RadioButton
    private lateinit var systemThemeRadio: RadioButton
    private lateinit var saveProfileButton: Button

    override fun onCreate(savedInstanceState: Bundle?) {
        themeManager = ThemeManager(this)
        // Apply saved theme BEFORE calling super.onCreate()
        themeManager.loadSavedTheme()

        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_profile)

        initializeViews()
        setupThemeSelection()
        loadCurrentTheme()
    }

    private fun initializeViews() {
        lightThemeRadio = findViewById(R.id.lightThemeRadio)
        darkThemeRadio = findViewById(R.id.darkThemeRadio)
        systemThemeRadio = findViewById(R.id.systemThemeRadio)
        saveProfileButton = findViewById(R.id.saveProfileButton)
    }

    private fun setupThemeSelection() {
        // Option 1: Change theme immediately when radio button is selected
        lightThemeRadio.setOnCheckedChangeListener { _, isChecked ->
            if (isChecked) {
                themeManager.setTheme(ThemeManager.THEME_LIGHT)
                recreateActivity()
            }
        }

        darkThemeRadio.setOnCheckedChangeListener { _, isChecked ->
            if (isChecked) {
                themeManager.setTheme(ThemeManager.THEME_DARK)
                recreateActivity()
            }
        }

        systemThemeRadio.setOnCheckedChangeListener { _, isChecked ->
            if (isChecked) {
                themeManager.setTheme(ThemeManager.THEME_SYSTEM)
                recreateActivity()
            }
        }

        // Option 2: Or change theme only when Save Profile is clicked
        saveProfileButton.setOnClickListener {
            saveThemeSelection()
            saveOtherProfileData()
            recreateActivity()
        }
    }

    private fun saveThemeSelection() {
        when {
            lightThemeRadio.isChecked -> themeManager.setTheme(ThemeManager.THEME_LIGHT)
            darkThemeRadio.isChecked -> themeManager.setTheme(ThemeManager.THEME_DARK)
            systemThemeRadio.isChecked -> themeManager.setTheme(ThemeManager.THEME_SYSTEM)
        }
    }

    private fun loadCurrentTheme() {
        // Set the correct radio button based on saved theme
        when (themeManager.getCurrentTheme()) {
            ThemeManager.THEME_LIGHT -> lightThemeRadio.isChecked = true
            ThemeManager.THEME_DARK -> darkThemeRadio.isChecked = true
            ThemeManager.THEME_SYSTEM -> systemThemeRadio.isChecked = true
        }
    }

    private fun recreateActivity() {
        // Smooth recreation
        finish()
        startActivity(intent)
        overridePendingTransition(android.R.anim.fade_in, android.R.anim.fade_out)
    }

    private fun saveOtherProfileData() {
        // Save your other profile data here
    }
