+import com.example.teacherscheduler.data.Repository
+import com.example.teacherscheduler.model.Semester
+import kotlinx.coroutines.launch
+
+class SemesterViewModel(application: Application) : AndroidViewModel(application) {
+    private val repository = Repository(application)
+    
+    val semesters: LiveData<List<Semester>> = repository.getAllActiveSemesters()
+    val currentSemester: LiveData<Semester?> = repository.getCurrentSemester()
+    
+    fun addSemester(semester: Semester) {
+        viewModelScope.launch {
+            repository.insertSemester(semester)
+        }
+    }
+    
+    fun updateSemester(semester: Semester) {
+        viewModelScope.launch {
+            repository.updateSemester(semester)
+        }
