+package com.example.teacherscheduler.ui
+
+import android.view.LayoutInflater
+import android.view.View
+import android.view.ViewGroup
+import androidx.recyclerview.widget.DiffUtil
+import androidx.recyclerview.widget.ListAdapter
+import androidx.recyclerview.widget.RecyclerView
+import com.example.teacherscheduler.databinding.ItemSemesterBinding
+import com.example.teacherscheduler.model.Semester
+
+class SemesterAdapter(
+    private val onItemClick: (Semester) -> Unit,
+    private val onSetCurrentClick: (Semester) -> Unit
+) : ListAdapter<Semester, SemesterAdapter.SemesterViewHolder>(SemesterDiffCallback()) {
+    
+    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): SemesterViewHolder {
+        val binding = ItemSemesterBinding.inflate(
+            LayoutInflater.from(parent.context),
+            parent,
+            false
+        )
+        return SemesterViewHolder(binding)
+    }
+    
+    override fun onBindViewHolder(holder: SemesterViewHolder, position: Int) {
+        holder.bind(getItem(position))
+    }
+    
+    inner class SemesterViewHolder(
+        private val binding: ItemSemesterBinding
+    ) : RecyclerView.ViewHolder(binding.root) {
+        
+        init {
+            binding.root.setOnClickListener {
+                val position = bindingAdapterPosition
+                if (position != RecyclerView.NO_POSITION) {
+                    onItemClick(getItem(position))
+                }
+            }
+            
+            binding.buttonSetCurrent.setOnClickListener {
+                val position = bindingAdapterPosition
+                if (position != RecyclerView.NO_POSITION) {
+                    onSetCurrentClick(getItem(position))
+                }
+            }
+        }
+        
+        fun bind(semester: Semester) {
+            binding.textViewSemesterName.text = semester.name
+            binding.textViewSemesterDuration.text = semester.getFormattedDuration()
+            
+            // Show/hide current semester indicator
+            binding.textViewCurrentSemester.visibility = 
+                if (semester.isCurrentSemester) View.VISIBLE else View.GONE
+            
+            // Disable "Set as Current" button if already current
+            binding.buttonSetCurrent.isEnabled = !semester.isCurrentSemester
+        }
+    }
+    
+    private class SemesterDiffCallback : DiffUtil.ItemCallback<Semester>() {
+        override fun areItemsTheSame(oldItem: Semester, newItem: Semester): Boolean {
+            return oldItem.id == newItem.id
+        }
+        
+        override fun areContentsTheSame(oldItem: Semester, newItem: Semester): Boolean {
+            return oldItem == newItem
+        }
+    }
+}

