+package com.example.teacherscheduler.ui.adapter
+
+import android.view.LayoutInflater
+import android.view.View
+import android.view.ViewGroup
+import android.widget.ImageButton
+import android.widget.TextView
+import androidx.recyclerview.widget.DiffUtil
+import androidx.recyclerview.widget.ListAdapter
+import androidx.recyclerview.widget.RecyclerView
+import com.example.teacherscheduler.R
+import com.example.teacherscheduler.model.Class
+
+class ClassAdapter(
+    private val onItemClick: (Class) -> Unit,
+    private val onDeleteClick: (Class) -> Unit
+) : ListAdapter<Class, ClassAdapter.ClassViewHolder>(ClassDiffCallback()) {
+
+    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ClassViewHolder {
+        val view = LayoutInflater.from(parent.context)
+            .inflate(R.layout.item_class, parent, false)
+        return ClassViewHolder(view)
+    }
+
+    override fun onBindViewHolder(holder: ClassViewHolder, position: Int) {
+        val classItem = getItem(position)
+        holder.bind(classItem)
+    }
+
+    inner class ClassViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
+        private val subjectText: TextView = itemView.findViewById(R.id.textSubject)
+        private val detailsText: TextView = itemView.findViewById(R.id.textDetails)
+        private val deleteButton: ImageButton = itemView.findViewById(R.id.buttonDelete)
+
+        fun bind(classItem: Class) {
+            subjectText.text = classItem.subject
+            detailsText.text = "${classItem.department} | ${classItem.roomNumber} | ${classItem.getFormattedTime()}"
+            
+            itemView.setOnClickListener {
+                onItemClick(classItem)
+            }
+            
+            deleteButton.setOnClickListener {
+                onDeleteClick(classItem)
+            }
+        }
+    }
+
+    private class ClassDiffCallback : DiffUtil.ItemCallback<Class>() {
+        override fun areItemsTheSame(oldItem: Class, newItem: Class): Boolean {
+            return oldItem.id == newItem.id
+        }
+
+        override fun areContentsTheSame(oldItem: Class, newItem: Class): Boolean {
+            return oldItem == newItem
+        }
+    }
+}

