package com.example.teacherscheduler.receiver

import android.app.AlarmManager
import android.app.PendingIntent
import android.content.BroadcastReceiver
import android.content.Context
import android.content.Intent
import android.os.Build
import com.example.teacherscheduler.model.NotificationType
import com.example.teacherscheduler.model.ScheduleNotification
import com.example.teacherscheduler.util.NotificationHelper
import java.util.Calendar

class NotificationActionReceiver : BroadcastReceiver() {
    override fun onReceive(context: Context, intent: Intent) {
        val notificationId = intent.getLongExtra(NotificationHelper.EXTRA_NOTIFICATION_ID, 0)

        // Get notification manager
        val notificationHelper = NotificationHelper(context)

        when (intent.action) {
            NotificationHelper.ACTION_SNOOZE -> {
                // Get notification type and reference ID
                val typeString = intent.getStringExtra(NotificationHelper.EXTRA_NOTIFICATION_TYPE) 
                    ?: NotificationType.CLASS.name
                val referenceId = intent.getLongExtra(NotificationHelper.EXTRA_REFERENCE_ID, 0)

                // Convert string to enum
                val type = try {
                    NotificationType.valueOf(typeString)
                } catch (e: IllegalArgumentException) {
                    NotificationType.CLASS
                }

                // Cancel the current notification
                notificationHelper.cancelNotification(notificationId)

                // Schedule a new notification 5 minutes from now
                val calendar = Calendar.getInstance()
                calendar.add(Calendar.MINUTE, 5)

                val notification = ScheduleNotification(
                    id = notificationId + 100, // Use a different ID to avoid conflicts
                    title = "Snoozed Reminder",
                    message = when (type) {
                        NotificationType.CLASS -> "Your class is starting soon"
                        NotificationType.MEETING -> "Your meeting is starting soon"
                    },
                    triggerTime = calendar.timeInMillis,
                    type = type,
                    referenceId = referenceId,
                    isEarlyReminder = true
                )

                // Schedule the snoozed notification
                val intent = Intent(context, AlarmReceiver::class.java).apply {
                    putExtra("notification_id", notification.id)
                    putExtra("notification_title", notification.title)
                    putExtra("notification_message", notification.message)
                    putExtra("notification_type", notification.type.name)
                    putExtra("reference_id", notification.referenceId)
                }

                val pendingIntent = PendingIntent.getBroadcast(
                    context,
                    notification.id.toInt(),
                    intent,
                    PendingIntent.FLAG_UPDATE_CURRENT or PendingIntent.FLAG_IMMUTABLE
                )

                // Schedule the alarm
                val alarmManager = context.getSystemService(Context.ALARM_SERVICE) as AlarmManager
                if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.M) {
                    alarmManager.setExactAndAllowWhileIdle(
                        AlarmManager.RTC_WAKEUP,
                        notification.triggerTime,
                        pendingIntent
                    )
                } else {
                    alarmManager.setExact(
                        AlarmManager.RTC_WAKEUP,
                        notification.triggerTime,
                        pendingIntent
                    )
                }
            }

            NotificationHelper.ACTION_DISMISS -> {
                // Just cancel the notification
                val notificationManager = context.getSystemService(Context.NOTIFICATION_SERVICE) 
                    as android.app.NotificationManager
                notificationManager.cancel(notificationId.toInt())
            }
        }
    }
}

