+        
+        if (itemId != -1L && itemType != null) {
+            when (itemType) {
+                NotificationHelper.TYPE_CLASS -> {
+                    // Switch to Classes tab
+                    binding.viewPager.currentItem = 0
+                    // Get the fragment and open the class details
+                    val fragment = getCurrentFragment() as? ClassesFragment
+                    fragment?.openClassDetails(itemId)
+                }
+                NotificationHelper.TYPE_MEETING -> {
+                    // Switch to Meetings tab
+                    binding.viewPager.currentItem = 1
+                    // Get the fragment and open the meeting details
+                    val fragment = getCurrentFragment() as? MeetingsFragment
+                    fragment?.openMeetingDetails(itemId)
+                }
+            }
+        }
+    }
